mapper.select:
SELECT * 
FROM wa_credit_info a 
INNER JOIN wa_credit b ON a.credit_id = b.credit_id 


		AND b.user_id = #{userId}


		AND a.credit_state >= #{geCreditState}


ORDER BY a.info_id  DESC
LIMIT ${start}, ${size}

SELECT COUNT(*) 
FROM wa_credit_info a 
INNER JOIN wa_credit b ON a.credit_id = b.credit_id  


		AND b.user_id = #{userId}


		AND a.credit_state >= #{geCreditState}
