mapper.select:
    SELECT * 
	FROM wa_tixian a 
	INNER JOIN wa_user b ON a.user_id = b.user_id 
	INNER JOIN wa_userinfo c ON b.user_id = c.user_id 


			AND b.user_name LIKE '%${userName}%'		


			AND a.tx_state = ${txState} 


			AND a.tx_date >= ${startDate}		


			AND a.tx_date <= ${endDate}		


	ORDER BY a.tx_id DESC
	LIMIT ${start}, ${size}

    SELECT COUNT(*) 
	FROM wa_tixian a 
	INNER JOIN wa_user b ON a.user_id = b.user_id 
	INNER JOIN wa_userinfo c ON b.user_id = c.user_id 


			AND b.user_name LIKE '%${userName}%'		


			AND a.tx_state = ${txState} 


			AND a.tx_date >= ${startDate}		


			AND a.tx_date <= ${endDate}
