mapper.select:
   SELECT  a.* , 
		b.user_name userName,  b.lixi_rate,
		c.company_yy_zz_img userImg,c.company_lxr userLxr, c.company_lxr_phone userLxrPhone,
		d.user_name creditUserName, 
		f.company_yy_zz_img creditUserImg,f.company_lxr creditUserLxr, f.company_lxr_phone creditUserLxrPhone
	FROM wa_credit a 
	LEFT JOIN wa_user b ON a.user_id = b.user_id 
	LEFT JOIN wa_userinfo c ON a.user_id = c.user_id 
	LEFT JOIN wa_user d ON a.credit_user_id = d.user_id 
	LEFT JOIN wa_userinfo f ON a.credit_user_id = f.user_id 


			a.credit_state >= #{gtCreditState}


			a.credit_state = #{creditState}


   			AND b.user_name LIKE '%${userName}%'


   			AND d.user_name LIKE '%${creditUserName}%'


   			AND a.credit_sq_date >= #{startCreditSqDate}


   			AND a.credit_sq_date <= #{endCreditSqDate}


   			AND a.credit_date >= #{startCreditDate}


   			AND a.credit_date <= #{endCreditDate}


   			AND a.credit_over_date >= #{startCreditOverDate}


   			AND a.credit_over_date <= #{endCreditOverDate}


   ORDER BY a.credit_sq_date DESC  
   LIMIT ${start}, ${size}

   SELECT COUNT(*) 
   FROM wa_credit a 
	LEFT JOIN wa_user b ON a.user_id = b.user_id 
	LEFT JOIN wa_userinfo c ON a.user_id = c.user_id 
	LEFT JOIN wa_user d ON a.credit_user_id = d.user_id 
	LEFT JOIN wa_userinfo f ON a.credit_user_id = f.user_id 


			a.credit_state >= #{gtCreditState}


			a.credit_state = #{creditState}


   			AND b.user_name LIKE '%${userName}%'


   			AND d.user_name LIKE '%${creditUserName}%'


   			AND a.credit_sq_date >= #{startCreditSqDate}


   			AND a.credit_sq_date <= #{endCreditSqDate}


   			AND a.credit_date >= #{startCreditDate}


   			AND a.credit_date <= #{endCreditDate}


   			AND a.credit_over_date >= #{startCreditOverDate}


   			AND a.credit_over_date <= #{endCreditOverDate}
