mapper.select:
SELECT *
from loan_order_view


        AND (customer_user_name LIKE '%${queryStr}%' OR creditor_user_name LIKE '%${queryStr}%' OR order_no LIKE '%${queryStr}%')



    order BY ${orderBy}


    LIMIT ${offset}, ${limit}

SELECT sum(loan_money)
from wa_loan
WHERE user_id=#{userId}
AND loan_state not in (-3,3);
