mapper.update:
UPDATE  wa_order

    confirm_price = #{confirmPrice},
    confirm_number = #{confirmNumber},
    sale_sj_num = #{saleSjNum},
    sale_sj_money = #{saleSjMoney},
    pj_state = #{pjState},
    pj_date = #{pjDate},
    pj_content = #{pj_Content},
    order_state = #{orderState},
    dj_modify_date = #{djModifyDate}
    has_question = #{hasQuestion}
    prod_pay_state = #{prodPayState}
    logistics_pay_state = #{logisticsPayState}
    prod_2ndpay_state=#{prod2ndpayState}
    logistics_2ndpay_state=#{logistics2ndpayState}
    kp_state=#{kpState}



        AND order_id = #{orderId}


        AND order_id in ${inOrderIds}

mapper.select:
SELECT a.*, b.prod_name, b.prod_img
FROM wa_order as a INNER JOIN wa_prod as b ON a.prod_id = b.prod_id
WHERE a.user_id = #{userId}

    AND a.order_state = #{orderState}

ORDER BY a.order_id DESC
LIMIT ${start} , ${size}

SELECT count(*)
FROM wa_order as a INNER JOIN wa_prod as b ON a.prod_id = b.prod_id
WHERE a.user_id = #{userId}

    AND a.order_state = #{orderState}

SELECT *,c.`prod_price`,(c.`prod_num`-c.`sale_num`) prod_num
FROM wa_order a INNER JOIN wa_prod c
ON a.`prod_id`=c.`prod_id`
WHERE a.order_state > -1

  and a.`supplier_id`=${supplierId}


    AND a.order_state=${orderState}

ORDER BY a.`user_id`

    LIMIT ${start},${size}


    LIMIT ${offset},${limit}

SELECT count(*)
from wa_order


        AND `supplier_id`=${supplierId}


        AND order_state=${orderState}


        AND user_id = #{userId}


        AND kp_state=#{kpState}

SELECT *
FROM orderinfoview
WHERE order_id=#{orderId}

select *
from orderinfoview


        AND user_id=#{userId}


        AND order_state=#{orderState}


        AND kp_state=#{kpState}


        AND order_id in ${orderIds}



      order by ${orderBy}


      limit #{offset},#{limit}

select order_no
from wa_order
where order_id=#{orderId}

SELECT a.*, b.prod_name, b.prod_img
FROM wa_order as a INNER JOIN wa_prod as b ON a.prod_id = b.prod_id
WHERE a.user_id = #{userId}

    AND a.order_state = #{orderState}

ORDER BY a.order_id DESC
LIMIT ${start} , ${size}

SELECT count(*)
FROM wa_order as a INNER JOIN wa_prod as b ON a.prod_id = b.prod_id
WHERE a.user_id = #{userId}

    AND a.order_state = #{orderState}

SELECT a.order_id, a.order_no, a.prod_id, a.sale_num, a.sale_money, a.sale_sj_num, a.sale_sj_money, a.order_state, a.order_date, b.prod_name, b.prod_img
FROM wa_order as a INNER JOIN wa_prod as b ON a.prod_id = b.prod_id
WHERE a.supplier_id = #{userId}

    AND a.order_state = #{orderState}

ORDER BY a.order_id DESC
LIMIT ${start} , ${size}

SELECT count(*)
FROM wa_order as a INNER JOIN wa_prod as b ON a.prod_id = b.prod_id
WHERE a.supplier_id = #{userId}

    AND a.order_state = #{orderState}

select GROUP_CONCAT(order_no)
from wa_order
WHERE order_id in ${inOrderIds}
