mapper.select:
SELECT a.* , b.user_name , c.company_img
FROM wa_user_money a
INNER JOIN wa_user b ON a.user_id = b.user_id
INNER JOIN wa_userinfo c ON a.user_id = c.user_id

    a.jy_state >= 0

        AND b.user_name LIKE '%${userName}%'


        AND a.jy_type = #{jyType}


        AND a.jy_date >= ${startJyDate}


        AND a.jy_date <= ${endJyDate}


ORDER BY a.money_id DESC
LIMIT ${offset}, ${limit}

SELECT COUNT(*)
FROM wa_user_money a
INNER JOIN wa_user b ON a.user_id = b.user_id

    a.jy_state >= 0

        AND b.user_name LIKE '%${userName}%'


        AND a.jy_type = #{jyType}


        AND a.jy_date >= ${startJyDate}


        AND a.jy_date <= ${endJyDate}

SELECT IFNULL(SUM(withdraw_money), 0.00) withdrawMoney
FROM wa_withdraw


        AND user_id = #{userId}


        AND withdraw_state >= #{geTxState}


        AND withdraw_state < #{ltTxState}


        AND withdraw_state = #{withdrawState}

SELECT *
FROM wa_withdraw


        AND user_id = #{userId}


        AND withdraw_state = #{withdrawState}


ORDER BY withdraw_date DESC
LIMIT ${start}, ${size}

SELECT COUNT(*)
FROM wa_withdraw


        AND user_id = #{userId}


        AND withdraw_state = #{withdrawState}

SELECT *
FROM wa_withdraw a
INNER JOIN wa_user b ON a.user_id = b.user_id
INNER JOIN wa_userinfo c ON b.user_id = c.user_id


        AND b.user_name LIKE '%${userName}%'


        AND a.withdraw_state = ${withdrawState}


        AND a.withdraw_date >= ${startDate}


        AND a.withdraw_date <= ${endDate}


ORDER BY a.withdraw_id DESC
LIMIT ${offset}, ${limit}

SELECT COUNT(*)
FROM wa_withdraw a
INNER JOIN wa_user b ON a.user_id = b.user_id
INNER JOIN wa_userinfo c ON b.user_id = c.user_id


        AND b.user_name LIKE '%${userName}%'


        AND a.withdraw_state = ${withdrawState}


        AND a.withdraw_date >= ${startDate}


        AND a.withdraw_date <= ${endDate}

SELECT a.order_id, a.bank_name, a.zf_money, a.zf_date, a.zf_type, a.zf_state, b.user_name FROM wa_bank_order AS a INNER JOIN wa_user AS b ON a.user_id = b.user_id
AND b.user_name LIKE '%${userName}%'
AND a.zf_state LIKE #{zfState}
ORDER BY a.order_id DESC LIMIT ${offset}, ${limit}

SELECT COUNT(*) FROM wa_bank_order AS a INNER JOIN wa_user AS b ON a.user_id = b.user_id
AND b.user_name LIKE '%${userName}%'
AND a.zf_state LIKE #{zfState}
