mapper.select:
SELECT * 
FROM wa_credit 


		AND user_id = #{userId}


		AND credit_state >= #{geCreditState}


		AND credit_state <= #{leCreditState}


		AND credit_state = #{creditState}


ORDER BY credit_id DESC
LIMIT ${start}, ${size}

SELECT COUNT(*) 
FROM wa_credit 


		AND user_id = #{userId}


		AND credit_state >= #{geCreditState}


		AND credit_state <= #{leCreditState}


		AND credit_state = #{creditState}
