mapper.select:
SELECT * FROM wa_user a INNER JOIN wa_userinfo b ON a.user_id = b.user_id WHERE a.user_state = 1
AND a.user_name LIKE '%${userName}%'
AND a.user_type = #{userType}
AND b.company_lxr LIKE '%${companyLxr}%'
AND b.company_lxr_phone LIKE '%${companyLxrPhone}%'
ORDER BY a.user_id LIMIT ${start}, ${size}
