mapper.select:
SELECT a.order_id, a.bank_name, a.zf_money, a.zf_date, a.zf_type, a.zf_state, b.user_name FROM wa_bank_order AS a INNER JOIN wa_user AS b ON a.user_id = b.user_id
AND b.user_name LIKE '%${userName}%'
AND a.zf_state LIKE #{zfState}
ORDER BY a.order_id DESC LIMIT ${start}, ${size}

SELECT COUNT(*) FROM wa_bank_order AS a INNER JOIN wa_user AS b ON a.user_id = b.user_id
AND b.user_name LIKE '%${userName}%'
AND a.zf_state LIKE #{zfState}
